function Notifier(){}$(document).ready(function(){$("span.help-inline.error").each(function(){$(this).parents("div.control-group").addClass("error")});$("ul.columns").each(function(){var e=$(this),t=e.clone();e.addClass("hidden-phone");t.addClass("visible-phone").removeClass("columns");e.after(t)})});Notifier.prototype.pending=function(e,t){$(".notifications .text").html(e);$(".notifications").removeClass("blocking");t===!0&&$(".notifications").addClass("blocking");$(".notifications").addClass("active");$(".notifications > div").attr("class","alert")};Notifier.prototype.success=function(e){e==null&&(e="Success!");$(".notifications .text").html(e);$(".notifications > div").attr("class","alert alert-success");setTimeout(function(){$(".notifications").removeClass("active")},400)};