$(document).ready(function(){$(".add-coauthor").click(function(e){e.preventDefault();var t=$(this),n=$(".coauthor:last").clone().removeClass("base");n.find("input").each(function(){$(this).val("");var e=$(this).attr("name"),t=$(this).attr("id"),n=parseInt(e.match(/[0-9]+/)[0]),r=n+1;e=e.replace(n,r);t=t.replace(n,r);$(this).attr({name:e,id:t})});var r=$("<a />").attr("href","#");r.addClass("btn btn-mini btn-danger close-btn");r.html("Remove");n.prepend(r);t.before(n);n.find("input:eq(0)").focus()});$(".btn.close-btn").live("click",function(e){e.preventDefault();$(this).parent().remove()})});