$(document).ready(function(){$(".coauthor.base").next("a.btn").click(function(e){e.preventDefault();var t=$(this),n=$(".right .coauthor:last").clone().removeClass("base");n.find("input").each(function(){$(this).val("");var e=$(this).attr("name"),t=$(this).attr("id"),n=parseInt(e.match(/[0-9]+/)[0]),r=n+1;e=e.replace(n,r);t=t.replace(n,r);$(this).attr({name:e,id:t})});t.before(n);n.find("input:eq(0)").focus()})});